import React, { useState } from "react";
import axios from "axios";

function App() {
  const [name, setName] = useState("");
  const [age, setAge] = useState(18);
  const [sex, setSex] = useState("");

  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  const handleAgeChange = (event) => {
    const age = parseInt(event.target.value);
    if (age < 18 || age > 99) {
      alert("Age must be between 18 and 99");
      return;
    }
    setAge(age);
  };

  const handleSexChange = (event) => {
    setSex(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    const locationData = await axios.get("/api/location");
    const country = locationData.data.location;

    await axios.post("/api/users", {
      name,
      age,
      sex,
      country,
    });

    setName("");
    setAge("");
    setSex("");
  };

  return (
    <div className="App">
      <h1>Sign Up</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Name:
          <input type="text" value={name} onChange={handleNameChange} />
        </label>
        <br />
        <label>
          Age:
          <input type="number" value={age} onChange={handleAgeChange} min="18" max="99" />
        </label>
        <br />
        <label>
          Sex:
          <input type="radio" value="male" checked={sex === "male"} onChange={handleSexChange} />
          Male
          <input type="radio" value="female" checked={sex === "female"} onChange={handleSexChange} />
          Female
        </label>
        <br />
        <button type="submit">Start</button>
      </form>
    </div>
  );
}

export default App;
